context.value:
(
    sm: (
        form-rows-spacing: $kendo-form-sm-rows-spacing
    ),
    md: (
        form-rows-spacing: $kendo-form-md-rows-spacing
    ),
    lg: (
        form-rows-spacing: $kendo-form-lg-rows-spacing
    )
)

context.code:
$_color: map.get($kendo-colors, $key);

@if ($_color) {
    @return var(--kendo-color-#{$key}, $_color);
} @else {
    @error "Color Variable \`#{$key}\` does not exists in the color collection.";
}
